mapper.select:
    	SELECT COUNT(token)
		  FROM resetpw_token
		 WHERE token = #{token}
		   AND email = #{email}

mapper.update:
UPDATE tbl_member
SET password = #{password}, updatedate = sysdate 
WHERE email = #{email}

mapper.select:
    	SELECT COUNT(nickname)
		  FROM tbl_member
		 WHERE nickname = #{nickname}

    	SELECT COUNT(email)
		  FROM tbl_member
		 WHERE email = #{email}
